const fs = require('fs')
const path = require('path')

const outPath = path.join(__dirname, '..', 'routeTree.gen.ts')

if (fs.existsSync(outPath)) {
  console.log('routeTree.gen.ts already exists â€” skipping')
  process.exit(0)
}

const content = `// This file is auto-generated by scripts/generate-route-stub.cjs
// It provides a minimal ` + "routeTree" + ` export so TypeScript and CI builds
// that require a generated route tree succeed. Replace with a proper
// generated file from your route generator when available.

export const routeTree: any = {}
`

fs.writeFileSync(outPath, content, { encoding: 'utf8' })
console.log('Wrote', outPath)
